'use strict';

var util = require('util');
var request = require('@root/request');
var initForm = require('./lib/init-form');
var isolateNotams = require('./lib/isolate-notams');
var endpoint = require('./lib/endpoint');

module.exports = function (_airports) {
  return new Promise(function (resolve, reject) {
    request.post({
      url: endpoint.href,
      form: initForm(_airports)
    }, function (err, resp, body) {
      if (err) {
        return reject(err);
      }

      if (resp.statusCode == 200) {
        resolve(isolateNotams(body));
      }
      else {
        return reject('got statusCode '+resp.statusCode);
      }
    });
  });
};
