'use strict';

var validateAirport = require('./validate-airport');

module.exports = function (_airports) {
  return {
    formatType:    'DOMESTIC',
    reportType:    'REPORT',
    openItems:     'icaosHeader,icaos:icaoHead,icao:rightNavSec0,rightNavSecBorder0:',
    actionType:    'notamRetrievalByICAOs',
    submit:        'View NOTAMs',
    retrieveLocId: arrayify(_airports).map(function (a) {
      return validateAirport(a);
    }).join(' ')
  };
};

function arrayify(_airports) {
  if (!Array.isArray(_airports)) {
    return [_airports];
  }
  return _airports;
}
