'use strict';

var cheerio = require('cheerio');
var isNotam = require('is-notam');

module.exports = function (body) {
  var $ = cheerio.load(body);

  return $('#notamRight > span').map(function () {
    var txt = $(this).text().trim();
    if (isNotam(txt)) {
      return txt;
    }
  }).get();
};
